import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import axios from "axios";
import tw from 'twin.macro';
import { useForm, SubmitHandler } from "react-hook-form";
import { IComment } from "../../../typings/comment";

interface ICommentProps extends IComment {}

const PageContainer = styled.div`
    ${tw`
        flex
        flex-col
        w-full
        h-full
        items-center
        overflow-x-hidden
    `}
`;

const FormComment = styled.form`
    ${tw`
        w-64
        h-40
        max-w-lg
        m-auto
        text-center
    `}
`;

const Text = styled.textarea`
    ${tw`
        border-solid
        border-gray-300
        border
        w-full
        rounded
        text-gray-700
    `}
`;

const Button = styled.button`
    ${tw`
        w-32
        mt-1
        text-white
        bg-red-500
        hover:bg-yellow-400
        border
        py-2
        px-4
        font-semibold
        rounded
    `}
`;

type FormValues = {
    text: string;
  };


export function CommentSection(props: {items: []}) {

    const [comments, setComments] = useState(props['items'] as any[]);
    const { register, handleSubmit } = useForm<FormValues>();
    console.log(comments)
    const onSubmit: SubmitHandler<FormValues> = (data) => {
        const newComments = [{author: 'Luca', text: data.text}, ...comments]
        setComments(newComments)
    };

    return (
        <PageContainer>
            <FormComment onSubmit={handleSubmit(onSubmit)}>
                <Text {...register("text")}/>
                <Button type="submit">Commenter</Button>
            </FormComment>
        </PageContainer>
    )
}
